import React, { useEffect } from 'react'
import { useState } from 'react';
import Carousel from 'react-bootstrap/Carousel';
import img from '../assets/carousel/carousel-img.png';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Container } from 'react-bootstrap';
import SecondCategoryList from '../layout/SecondCategoryList';
import OwlCarousel from 'react-owl-carousel';
import 'owl.carousel/dist/assets/owl.carousel.css';
import 'owl.carousel/dist/assets/owl.theme.default.css';
import ProductCard from '../components/ProductCard';
import productService from '../services/productService';

export default function Home() {
	const [products, setProducts] = useState([]);

	useEffect(() => {
		let productService1 = new productService();
		productService1.getProducts().then(result => setProducts(result.data.data));
	})

	return (
		<div className='home'>

			<div className='home-page-carousels p-4'>
				<SecondCategoryList />
				<Container>
					<Carousel>
						<Carousel.Item>
							<img src={img} alt='carousel slide' className='w-100 d-block' />
							<Carousel.Caption>
								<h3>First slide label</h3>
								<p>Nulla vitae elit libero, a pharetra augue mollis interdum.</p>
							</Carousel.Caption>
						</Carousel.Item>
						<Carousel.Item>
							<img src={img} alt='carousel slide' className='w-100 d-block' />
							<Carousel.Caption>
								<h3>Second slide label</h3>
								<p>Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
							</Carousel.Caption>
						</Carousel.Item>
						<Carousel.Item>
							<img src={img} alt='carousel slide' className='w-100 d-block' />
							<Carousel.Caption>
								<h3>Third slide label</h3>
								<p>
									Praesent commodo cursus magna, vel scelerisque nisl consectetur.
								</p>
							</Carousel.Caption>
						</Carousel.Item>
					</Carousel>

					<OwlCarousel className='owl-theme mt-4' loop margin={10} nav dots={false}>

						{
							[null, null, null, null, null, null].map((item) => (
								<div class='item'>
									<img src={img} />
								</div>
							))
						}

					</OwlCarousel>

				</Container>


			</div>


			<div>
				<Container className='p-4'>
					<p className='h5 text-center mb-4'>
						Good Deals
					</p>

					<div className='row'>
						{
							products.filter((item, index) => index < 4).map(product => (
								<div className='col-md-3 col-6 cd-flex justify-content-center'>
									<ProductCard product={product} />

								</div>

							))
						}

					</div>
				</Container>

			</div>

		</div>


	);
}
