import React, { useEffect, useState } from 'react'
import img1 from '../assets/product-images/product-image.png'
import img2 from '../assets/main-page/banners/banner1.png'
import img3 from '../assets/main-page/banners/banner2.png'
import img4 from '../assets/main-page/banners/banner3.png'
import img5 from '../assets/main-page/banners/banner4.png'
import img6 from '../assets/main-page/banners/banner5.png'
import { useParams } from 'react-router-dom';
import productService from '../services/productService';
import { Container, Accordion } from 'react-bootstrap';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Navigation, Pagination, Thumbs } from 'swiper/modules';
import 'swiper/css';
import 'swiper/css/navigation';
import 'swiper/css/pagination';
import 'swiper/css/scrollbar';

export default function ProductDetail(props) {

	let { id } = useParams();

	const [product, setProduct] = useState({});
	const [activeThumb, setActiveThumb] = useState();


	useEffect(() => {
		let productService1 = new productService();
		productService1.getProductById(id).then(result => setProduct(result.data.data));

	}, [])


	let productPhotos = [img1, img2, img3, img4, img5, img6];

	let photos = [
		require('../assets/product-images/product-image.png'),
		require('../assets/main-page/banners/banner1.png'),
		require('../assets/main-page/banners/banner2.png'),
		require('../assets/main-page/banners/banner3.png'),
		require('../assets/main-page/banners/banner4.png'),
		require('../assets/main-page/banners/banner5.png')
	]

	return (
		<div className='background-grey'>
			<Container>
				<div className='row'>
					<div className='col-12 col-lg-6'>

						<div className='product-images-swiper'>
							<Swiper
								loop={true}
								spaceBetween={10}
								navigation={true}
								grabCursor={true}
								thumbs={{ swiper: activeThumb }}
								modules={[Navigation, Thumbs]}
								className='product-images-slider'
							>
								{
									photos.map((item, index) => (
										<SwiperSlide key={index}>
											<img src={item} alt='product images' />
										</SwiperSlide>
									))
								}
							</Swiper>
							<Swiper
								onSwiper={setActiveThumb}
								loop={true}
								spaceBetween={10}
								slidesPerView={4}
								modules={[Navigation, Thumbs]}
								className='product-images-slider-thumbs'
							>
								{
									photos.map((item, index) => (
										<SwiperSlide key={index}>
											<div className='product-images-slider-thumbs-wrapper'>
												<img src={item} alt='product images' />
											</div>


										</SwiperSlide>
									))
								}
							</Swiper>
						</div>

						<Accordion defaultActiveKey="0" flush className='my-4'>
							<Accordion.Item eventKey="0" className='my-2'>
								<Accordion.Header className='product-detail-accordion-header'>Accordion Item #1</Accordion.Header>
								<Accordion.Body>
									Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do
									eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad
									minim veniam, quis nostrud exercitation ullamco laboris nisi ut
									aliquip ex ea commodo consequat. Duis aute irure dolor in
									reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla
									pariatur. Excepteur sint occaecat cupidatat non proident, sunt in
									culpa qui officia deserunt mollit anim id est laborum.
								</Accordion.Body>
							</Accordion.Item>
						</Accordion>
					</div>
				</div>
			</Container>

		</div>
	)
}
