import React, { useEffect, useState } from 'react'
import img1 from '../assets/product-images/product-image.png'
import img2 from '../assets/main-page/banners/banner1.png'
import img3 from '../assets/main-page/banners/banner2.png'
import img4 from '../assets/main-page/banners/banner3.png'
import img5 from '../assets/main-page/banners/banner4.png'
import img6 from '../assets/main-page/banners/banner5.png'
import { useParams } from 'react-router-dom';
import productService from '../services/productService';
import { Container } from 'react-bootstrap';
import OwlCarousel from 'react-owl-carousel';
import 'owl.carousel/dist/assets/owl.carousel.css';
import 'owl.carousel/dist/assets/owl.theme.default.css';

export default function ProductDetail() {

	let { id } = useParams();

	const [product, setProduct] = useState({});

	useEffect(() => {
		let productService1 = new productService();
		productService1.getProductById(id).then(result => setProduct(result.data.data));

	}, [])


	let productPhotos = [img1]

	return (
		<div className='background-grey'>
			<Container>
				<div className='row'>
					<div className='col-12 col-lg-6'>
						<img src={img1} className='img-fluid w-100' alt='product image' />
						<OwlCarousel className='owl-theme mt-4' loop margin={10} nav dots={false}>

							{
								[null, null, null, null, null, null].map((item) => (
									<div class='item'>
										<img src={img1} />
									</div>
								))
							}

						</OwlCarousel>
					</div>
				</div>
			</Container>

		</div>
	)
}
