import React, { useEffect, useState } from 'react'
import img from '../assets/product-images/product-image.png'
import { useParams } from 'react-router-dom';
import productService from '../services/productService';
import { Container } from 'react-bootstrap';

export default function ProductDetail() {

	let { id } = useParams();

	const [product, setProduct] = useState({});

	useEffect(() => {
		let productService1 = new productService();
		productService1.getProductById(id).then(result => setProduct(result.data.data));

	}, [])

  return (
	<div className='background-grey'>
	<Container>

	</Container>
		<div className='row'>
			<div className='col-12 col-lg-6'>
				<img src={img} className='img-fluid' alt='product image' />
			</div>
		</div>
	</div>
  )
}
